const text = $json["ParsedResults"][0].ParsedText;

// FunÃ§Ã£o helper para regex com fallback
const extract = (pattern) => {
  const match = text.match(pattern);
  return match ? match[1].trim() : null;
};

// FunÃ§Ã£o helper para detectar nome
const extractNome = () => {
  const nomeMatch = text.match(/(?:AUTORIZADO COM SENHA|Pagamento autorizado com senha)\s*\n([\w\sÃ€-Ã¿]+)/i);
  return nomeMatch ? nomeMatch[1].trim() : null;
};

let nome = extractNome();

// Detectar tipo de transaÃ§Ã£o (crÃ©dito, dÃ©bito, PIX)
let tipo = "DINHEIRO";
if (/CREDITO|CRÃ‰DITO/i.test(text)) tipo = "CREDITO";
else if (/DEBITO|DÃ‰BITO/i.test(text)) tipo = "DEBITO";
else if (/PIX/i.test(text)) tipo = "PIX";

// Data/hora no formato dd/MM/yyyy HH:mm
// Data/hora no formato ISO para Supabase
let dataHora = extract(/(\d{2}\/\d{2}\/\d{2,4}\s*\d{2}[:\s]\d{2})/i);

if (dataHora) {
  const parts = dataHora.match(/(\d{2})\/(\d{2})\/(\d{2,4})\s*(\d{2})[:\s](\d{2})/);
  if (parts) {
    const dia = parts[1];
    const mes = parts[2];
    const ano = parts[3].length === 2 ? '20' + parts[3] : parts[3]; // transforma 25 em 2025
    const hora = parts[4];
    const min = parts[5];
    // muda para ISO
    dataHora = `${ano}-${mes}-${dia} ${hora}:${min}:00`;
  }
}


// Valor: prioriza "VALOR PAGO / A PAGAR / TOTAL"
let valor = null;
const regexValorPrioritario = /(VALOR\s*(?:PAGO|A\s*PAGAR|TOTAL)[^\d]*(\d+[\.,]\d{2}))/i;
const matchPrioritario = text.match(regexValorPrioritario);

if (matchPrioritario) {
  valor = matchPrioritario[2].replace(',', '.');
} else {
  valor = extract(/R\$\s*([\d.,]+)/);
  if (valor) valor = valor.replace(',', '.');
}

// Parcelas: procura se tem "2x", "3x" etc.
const parcelas = text.match(/(\d+)[xX]/);
if (parcelas && valor) {
  valor = `${valor} (${parcelas[1]}x)`;
}

// Monta resultado no formato pedido
const resultado = {
  nome: nome || null,
  dataHora,
  valor,
  tipo
};

return [
  { json: resultado }
];

