function formatDataHoraBrasilISO() {
  const now = new Date();

  // Converte para UTC e aplica -3h (BrasÃ­lia)
  const utc = now.getTime() + now.getTimezoneOffset() * 60000;
  const brasil = new Date(utc - 3 * 60 * 60 * 1000);

  // Retorna formato aceito pelo Supabase
  const pad = (n) => n.toString().padStart(2, "0");
  return `${brasil.getFullYear()}-${pad(brasil.getMonth() + 1)}-${pad(brasil.getDate())} ${pad(brasil.getHours())}:${pad(brasil.getMinutes())}:${pad(brasil.getSeconds())}`;
}

